from django.db import models
from decimal import Decimal
from django.contrib.auth.models import User

# from drf_extra_fields.fields import Base64ImageField

# Create your models here.


class Product(models.Model):
    
    title = models.CharField(max_length=100, blank=True, default='')
    image = models.TextField(max_length=100000, default='data:image/png;base64,')
    description = models.TextField()
    price = models.DecimalField(max_digits=10, decimal_places=2, default=Decimal('0.00'))
    quantity = models.PositiveIntegerField(default=1)
    created = models.DateTimeField(auto_now_add=True)
    user_id = models.ForeignKey(User, on_delete=models.CASCADE, null=True)

    class Meta:
        ordering = ('created',)

class Comment(models.Model):

    content = models.TextField()
    created = models.DateTimeField(auto_now_add=True)
    user_id = models.ForeignKey(User, on_delete=models.CASCADE, null=True)
    product_id = models.ForeignKey(Product, on_delete=models.CASCADE, null=True, blank=False)
    created = models.DateTimeField(auto_now_add=True)
    updated = models.DateTimeField(auto_now=True)
    edited = models.BooleanField(default = False)
    parent_id = models.IntegerField(null=True)
    profile_image = models.TextField(max_length=100000, default='data:image/png;base64,')
class Cart(models.Model):

    title = models.CharField(max_length=100, blank=True, default='')
    image = models.TextField(max_length=100000, default='data:image/png;base64,')
    description = models.TextField(default='')
    price = models.DecimalField(max_digits=10, decimal_places=2, default=Decimal('0.00'))
    quantity = models.PositiveIntegerField(default=1)
    created = models.DateTimeField(auto_now_add=True)
    user_id = models.ForeignKey(User, on_delete=models.CASCADE, null=True)
    product_id = models.IntegerField(null=True)


class ProfilePicture(models.Model):

    user_id = models.ForeignKey(User, on_delete=models.CASCADE, null=True)
    image = models.TextField(max_length=100000, default='data:image/png;base64,')